import React, { Component, useState } from 'react';
import TrelloCard from './TrelloCard';
import TrelloActionButton from './TrelloActionButton';
import TodoModal from './TodoModal';
import Modal from './Modal';

class TrelloList extends Component { 

    render() {
        const {title, cards, listID} = this.props;
        const [state, setState] = useState(false); // 현재 state, 이 값을 업데이트 하는 함수

        console.log(state);
        // const handleModal = ({listID, id}) => {
        //     console.log(modal);
        //     setModal(!modal)
        // }

        return ( 
            <div style={style.container}> 
                <h4>{title}</h4> 
                {cards.map(({text}, id) => (
                    <TrelloCard key={id} text={text} onClick={() => {setState(true)}}></TrelloCard>
                ))}
                {
                    state && (
                        <Modal>
                            <TodoModal></TodoModal>
                        </Modal>
                    )
                }
                <TrelloActionButton listID={listID}></TrelloActionButton>
            </div>
        ); 
    }
}; 
const style = { 
    container: { 
        backgroundColor: '#dfe3e6', 
        borderRadius: 3, 
        width: 300, 
        padding: 8, 
        marginRight: 8 
    } 
};

export default TrelloList;
