import React, { Component, useState } from 'react';
import TrelloCard from './TrelloCard';
import TrelloActionButton from './TrelloActionButton';
import TodoModal from './TodoModal';
import Modal from './Modal';

const TrelloList = ({title, cards, listID}) => {
    const [state, setState] = useState(false); // 현재 state, 이 값을 업데이트 하는 함수
    const [modal, setModal] = useState(false); // 현재 state, 이 값을 업데이트 하는 함수

    const handleModal = () => {
        console.log(modal);
        setModal(!modal)
    }

    return ( 
        <div style={style.container} onClick={handleModal}> 
            <h4>{title}</h4> 
            {cards.map(({text}, id) => (
                <TrelloCard key={id} text={text}></TrelloCard>
            ))
            }
            {
                modal && (
                    <Modal>
                        <TodoModal></TodoModal>
                    </Modal>
                )
            }
            <TrelloActionButton listID={listID}></TrelloActionButton>
        </div>
    ); 
}
