import React, { useState } from 'react'; 
import Card from '@material-ui/core/Card'; 
import Icon from '@material-ui/core/Icon'
import Button from '@material-ui/core/Button'
import Textarea from 'react-textarea-autosize'
import Typography from '@material-ui/core/Typography'; 
import CardContent from '@material-ui/core/CardContent';
import './Trello.css';
import TodoModal from './TodoModal';
import Modal from './Modal';

const TrelloCard = ({ text, body }) => {
    const [modal, setModal] = useState(false); // 현재 state, 이 값을 업데이트 하는 함수
    const [state, setState] = useState(false); // 현재 state, 이 값을 업데이트 하는 함수
    const [edit, setEdit] = useState(false); 

    const handleModal = () => {
        setModal(!modal)
    }

    // 수정 창 띄우기 
    const editMode = () => {
        const titleText = text;

        const handleTitleUpdate = ({target}) => {
            setEdit(target.value)
        }

        return ( 
            <div> 
                <Card style={styles.Card}> 
                    <Textarea style={styles.Textarea} placeholder={titleText} 
                    autoFocus value={text}
                    onChange={handleTitleUpdate}/> 
                </Card> 
                <div style={styles.formButtonGroup}> 
                    <Button style={styles.Button} variant="contained"
                    onClick={() => {setState(false)}}>
                        SAVE
                    </Button> 
                    <Icon>close</Icon> 
                </div> 
            </div>
        );
    } 

    return (
        <Card style = {styles.CardContainer}>
            <div className="todo-item">
                {
                    state ? 
                    <CardContent className="todo-text" onClick={() => handleModal()}>
                        <Typography gutterBottom>{text}</Typography>
                    </CardContent>
                    : editMode()
                }
                <CardContent className="todo-text" onClick={() => handleModal()}>
                    <Typography gutterBottom>{text}</Typography>
                </CardContent>
                {
                    modal && (
                        <Modal>
                            <TodoModal handleModal={handleModal} title={text} body={body}></TodoModal>
                        </Modal>
                    )
                }
                <div className="update" onClick={() => {setState(true)}}>&#9998;</div> 
            </div>
        </Card>
    )
}

const styles = {
    CardContainer: {
        marginBottom: 10
    }
}
export default TrelloCard;
