import React, { Component } from 'react';
import TrelloCard from './TrelloCard';
import TrelloActionButton from './TrelloActionButton';

class TrelloList extends Component { 
    state = {
       modal : false
    }

    // handleModal = ({listID, id}) => {
    //     setModal(!this.state.modal)
    // }

    render() {
        const {title, cards, listID} = this.props;
        const [modal, setModal] = useState(false); // modal 창 열고 닫기

        const handleModal = ({listID, id}) => {
            setModal(!modal)
        }

        return ( 
            <div style={style.container}> 
                <h4>{title}</h4> 
                {cards.map(({text}, id) => (
                    <TrelloCard key={id} text={text}></TrelloCard>
                ))}
                <TrelloActionButton listID={listID}></TrelloActionButton>
            </div>
        ); 
    }
}; 
const style = { 
    container: { 
        backgroundColor: '#dfe3e6', 
        borderRadius: 3, 
        width: 300, 
        padding: 8, 
        marginRight: 8 
    } 
};

export default TrelloList;
