import React, { useState } from 'react'; 
import Card from '@material-ui/core/Card'; 
import Icon from '@material-ui/core/Icon'
import Button from '@material-ui/core/Button'
import Textarea from 'react-textarea-autosize'
import Typography from '@material-ui/core/Typography'; 
import CardContent from '@material-ui/core/CardContent';
import './Trello.css';
import TodoModal from './TodoModal';
import Modal from './Modal';

const TrelloCard = ({ text, body }) => {
    const [modal, setModal] = useState(false); // 현재 state, 이 값을 업데이트 하는 함수
    const [isEdit, setEdit] = useState(false); // 현재 state, 이 값을 업데이트 하는 함수
    const [edit, setUpdate] = useState(''); 

    const handleModal = () => {
        setModal(!modal)
    }

    // 수정 창 띄우기 
    const editMode = () => {
        const titleText = text;

        const handleTitleUpdate = ({target}) => {
            setUpdate(target.value)
        }

        return ( 
            <div> 
                <Card style={styles.Card}> 
                    <Textarea style={styles.Textarea} placeholder={titleText} 
                    autoFocus value={text}
                    onChange={handleTitleUpdate}/> 
                </Card> 
                <div style={styles.formButtonGroup}> 
                    <Button style={styles.Button} variant="contained"
                    onClick={() => {setEdit(false)}}>
                        SAVE
                    </Button> 
                    <Icon>close</Icon> 
                </div> 
            </div>
        );
    } 

    const readMode = () => {
        return (
            <Card style = {styles.CardContainer}>
                <div className="todo-item">
                    <CardContent className="todo-text" onClick={() => handleModal()}>
                        <Typography gutterBottom>{text}</Typography>
                    </CardContent>
                    {
                        modal && (
                            <Modal>
                                <TodoModal handleModal={handleModal} title={text} body={body}></TodoModal>
                            </Modal>
                        )
                    }
                    <div className="update" onClick={() => {setEdit(true)}}>&#9998;</div> 
                </div>
            </Card>
        );
    }

    return (
        <div>
            {
                isEdit === false ? readMode() : editMode()
            }
        </div>

    )
}

const styles = { 
    CardContainer: {
        marginBottom: 10
    },

    openFormButtonGroup:{ 
        display:"flex", 
        alignItems:"center", 
        cursor:"pointer", 
        borderRadius:3, 
        height:36, 
        width:272, 
        paddingLeft:10 
    }, 
    Card: {
        overflow:"visible", 
        minHeight:80, 
        minWidth:272, 
        padding:"6px 8px 2px"
    },
    Textarea: { 
        resize:"none", 
        overflow:"hidden", 
        outline:"none", 
        border:"none", 
        width:"100%" 
    }, 
    Button: {
        color:"white", 
        backgroundColor: "#5aac44",
        marginBottom: 10
    },
    Icon: {
        marginLeft:8, 
        cursor:"pointer"
    },
    formButtonGroup:{ 
        marginTop:8, 
        display:"flex", 
        alignItems:"center" 
    }
}
export default TrelloCard;
