import { CONSTANTS } from "../actions";

const modalReducer = (state = {}, action) => {
    switch(action.type) {
        case CONSTANTS.EDIT_CARD:
            const { cardID, cardText, listID } = action.payload;
            const editState = state.map(list => {
                if(list.id === listID) {
                    return {
                        ...list,
                        cards: list.cards.map(item =>
                            item.id === cardID ? {
                                ...item,
                                text: cardText
                            } : item
                        )
                    }
                } else return list
            })
            return editState;

        default: return state;
    }
};

export default modalReducer;
